import Carousel from 'react-bootstrap/Carousel';
import React, { useState } from 'react';
import MediaQuery from 'react-responsive';
import { Fragment } from "react";


function Doni() {
    const [index, setIndex] = useState(0);
  
    const handleSelect = (selectedIndex, e) => {
      setIndex(selectedIndex);
    };

    return (
        <Carousel activeIndex={index} onSelect={handleSelect}>
          <Carousel.Item>
            <img
            style={{width: '100%', height: '600px', marginLeft: 'auto', marginRight: 'auto'}}
              className="d-block"
              src="https://imgv3.fotor.com/images/homepage-feature-card/enhance-photo-of-three-girls-playing-in-the-sunflower-garden.png"
              alt="First slide"
            />
            <Carousel.Caption>
              <h3>First slide label</h3>
              <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
            style={{width: '100%', height: '600px',marginLeft: 'auto', marginRight: 'auto'}}
              className="d-block"
              src="https://imgv3.fotor.com/images/homepage-feature-card/enhance-photo-of-three-girls-playing-in-the-sunflower-garden.png"
              alt="Second slide"
            />
            <Carousel.Caption>
              <h3>Second slide label</h3>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
            </Carousel.Caption>
          </Carousel.Item>
          <Carousel.Item>
            <img
            style={{width: '100%', height: '600px', marginLeft: 'auto', marginRight: 'auto'}}
              className="d-block"
              src="https://imgv3.fotor.com/images/homepage-feature-card/enhance-photo-of-three-girls-playing-in-the-sunflower-garden.png"
              alt="Third slide"
            />
    
            <Carousel.Caption>
              <h3>Third slide label</h3>
              <p>
                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
              </p>
            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
      );
    }

function Crsl() {
  return (
    <Fragment>
    <MediaQuery minDeviceWidth={701}>
    <div style={{  margin: 'auto', width: '80%'}}>
        <h3 style={{textAlign: 'center'}}>Disa nga ceshtjet tona te mbyllura</h3>
    <Doni />
    </div>
    </MediaQuery>

    <MediaQuery maxDeviceWidth={700} >
    <div style={{  margin: 'auto', width: '80%'}}>
        <h3 style={{textAlign: 'center'}}>Disa nga ceshtjet tona te mbyllura</h3>
    <Doni />
    </div>
    </MediaQuery>
    </Fragment>
  );
}

export default Crsl;
