import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
// import NavDropdown from 'react-bootstrap/NavDropdown';
import { useEffect, useState } from 'react';


function Navbar1() {
  const [sticky, setSticky] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setSticky(window.scrollY > 200)
    }
    window.addEventListener('scroll', handleScroll)
    return () => window.removeEventListener('scroll', handleScroll);
  })
  
  useEffect(() => {
    document.getElementById('navbar').scrollIntoView();
  }, [])

  return (
    <Navbar className={`${sticky ? "sticky" : ""}`} id="navbar" collapseOnSelect 
    expand="lg" variant="dark" 
    style = {{
      background: 'linear-gradient(to right, blue ,lightblue)', 
      boxShadow: "0px 5px 18px rgba(0,0,0,0.6)", top: "0px", 
      zIndex: "300", position: 'sticky', }}>
      <Container>
        <Navbar.Brand href="#home">Edison Lulzim Basha</Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="#sherbimet">SHerbimet</Nav.Link>
            <Nav.Link href="#ceshtjet">Ceshtjet</Nav.Link>
            <Nav.Link href="#pricing">Doniiii</Nav.Link>
            {/* <NavDropdown title="Dropdown" id="collasible-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">
                Another action
              </NavDropdown.Item>
              <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">
                Separated link
              </NavDropdown.Item>
            </NavDropdown> */}
          </Nav>
          <Nav>
            <Nav.Link href="#deets">Me shume</Nav.Link>
            <Nav.Link eventKey={2} href="#memes">
              Edisoni do grua
            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Navbar1;
