import shqpr from "../components/assets/shqpr.jpg";
import ali from '../components/assets/ali.png'

const Section2 = () => {
  return (
    <div
      style={{
        width: "100%",
        margin: "0 auto",
        position: "relative",
        backgroundRepeat: "no-repeat",
      }}
    >
      <p style={{textAlign: 'center'}}>
        Hartimin e të gjitha dokumenteve të nevojshme për regjistrimin e
        biznesit në Shqipëri, përgatitjen e Rezolutës së Bordit të Drejtorëve,
        Prokurës dhe Dokumenteve të tjera të kërkuara për këtë qëllim;
      </p>
      <div
        style={{
          backgroundImage: `url(${ali})`,
          textAlign: "center",
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center center",
        }}
      >
        <h3
          style={{
            paddingBottom: "500px",
            paddingTop: "35px",
            width: "100%",
            textAlign: "center",
            color: 'black',
          }}
        >
          Hartimin e të gjitha dokumenteve <br />
          -Regjistrime të Aplikimeve në Qendrën Kombëtare të Biznesit, on-line. <br />
          -Këshilla Ligjore. <br />
          -Hartimin e dokumentave të ndryshme ligjore; <br />
          -Hartimin dhe negocimin e kontratave te ndryshme <br />
          -Leje pune dhe leje qendrimi per shtetasit e huaj <br />
          -Likujdimin e shoqerise <br />
        </h3>

        {/* <h3>-Këshilla Ligjore për të Drejtën Tregtare, Kodin e Punes, Ligjin për të Huajt dhe ligje te tjera, si dhe për çështje të ndryshme të biznesit;  

-Hartimin e dokumentave të ndryshme ligjore;

-Hartimin dhe negocimin e kontratave te ndryshme (shitjeje, qeraje, kontrata pune, kontratë shërbimi etj…), Ndryshime të Kontratave Ekzistuese;

-Leje pune dhe leje qendrimi per shtetasit e huaj;

-Likujdimin e shoqerise;

-Shkresa te llojeve te ndryshme per ne administrate shteterore. 

- Çdo gjë që ka te beje ne çeshtjet e biznesit por nga ana ligjore.
</h3> */}
      </div>
    </div>
  );
};

export default Section2;
